import { useEffect, useMemo, useState } from 'react';
import { addTransaction, fetchBudget, fetchTransactions, resetTransactions, setBudget, setToken } from '../api';
import { Pie, Bar, Line } from 'react-chartjs-2';
import {
	Chart as ChartJS,
	ArcElement,
	Tooltip,
	Legend,
	CategoryScale,
	LinearScale,
	BarElement,
	PointElement,
	LineElement,
	Title
} from 'chart.js';

ChartJS.register(
	ArcElement,
	Tooltip,
	Legend,
	CategoryScale,
	LinearScale,
	BarElement,
	PointElement,
	LineElement,
	Title
);

export default function Dashboard({ onLogout }) {
	const [transactions, setTransactions] = useState([]);
	const [budget, setBudgetState] = useState(0);
	const [loading, setLoading] = useState(true);
	const [form, setForm] = useState({
		description: '',
		amount: '',
		type: 'expense',
		category: 'housing'
	});

	const totalIncome = useMemo(
		() => transactions.filter(t => t.type === 'income').reduce((s, t) => s + t.amount, 0),
		[transactions]
	);
	const totalExpenses = useMemo(
		() => transactions.filter(t => t.type === 'expense').reduce((s, t) => s + t.amount, 0),
		[transactions]
	);
	const balance = useMemo(() => totalIncome - totalExpenses, [totalIncome, totalExpenses]);

	useEffect(() => {
		async function load() {
			try {
				const [txs, b] = await Promise.all([fetchTransactions(), fetchBudget()]);
				setTransactions(Array.isArray(txs) ? txs : []);
				setBudgetState(b?.amount || 0);
			} catch {
				// ignore here; UI will show empty
			} finally {
				setLoading(false);
			}
		}
		load();
	}, []);

	function logout() {
		setToken(null);
		onLogout?.();
	}

	async function handleAdd(e) {
		e.preventDefault();
		const amount = parseFloat(form.amount);
		if (!form.description || isNaN(amount) || amount <= 0) return;
		try {
			const created = await addTransaction({
				description: form.description,
				amount,
				type: form.type,
				category: form.category
			});
			if (created?._id) {
				setTransactions(prev => [created, ...prev]);
				setForm({ description: '', amount: '', type: 'expense', category: 'housing' });
			}
		} catch {
			// optional: toast
		}
	}

	async function handleSetBudget() {
		const amount = parseFloat(prompt('Set monthly budget (₹):', `${budget || 0}`) || '0');
		if (isNaN(amount) || amount < 0) return;
		try {
			const b = await setBudget(amount);
			setBudgetState(b?.amount || 0);
		} catch {}
	}

	async function handleReset() {
		if (!window.confirm('Delete all transactions?')) return;
		try {
			await resetTransactions();
			setTransactions([]);
		} catch {}
	}

	const expenseByCategory = useMemo(() => {
		const m = transactions
			.filter(t => t.type === 'expense')
			.reduce((acc, t) => {
				acc[t.category] = (acc[t.category] || 0) + t.amount;
				return acc;
			}, {});
		return m;
	}, [transactions]);

	const pieData = useMemo(() => {
		const labels = Object.keys(expenseByCategory);
		const data = labels.map(l => expenseByCategory[l]);
		return {
			labels,
			datasets: [
				{
					data,
					backgroundColor: ['#60A5FA', '#34D399', '#F87171', '#FBBF24', '#A78BFA', '#FB923C'],
					borderColor: '#fff',
					borderWidth: 2
				}
			]
		};
	}, [expenseByCategory]);

	const barData = useMemo(() => {
		const byMonth = {};
		transactions.forEach(t => {
			const d = new Date(t.timestamp);
			const key = `${d.getFullYear()}-${String(d.getMonth() + 1).padStart(2, '0')}`;
			if (!byMonth[key]) byMonth[key] = { income: 0, expense: 0 };
			byMonth[key][t.type] += t.amount;
		});
		const labels = Object.keys(byMonth).sort();
		return {
			labels,
			datasets: [
				{ label: 'Income', data: labels.map(l => byMonth[l].income), backgroundColor: '#34D399' },
				{ label: 'Expenses', data: labels.map(l => byMonth[l].expense), backgroundColor: '#F87171' }
			]
		};
	}, [transactions]);

	const balanceLineData = useMemo(() => {
		const items = [...transactions].sort((a, b) => new Date(a.timestamp) - new Date(b.timestamp));
		let running = 0;
		const labels = [];
		const data = [];
		items.forEach(t => {
			running += t.type === 'income' ? t.amount : -t.amount;
			labels.push(new Date(t.timestamp).toLocaleDateString());
			data.push(running);
		});
		return {
			labels,
			datasets: [
				{
					label: 'Balance',
					data,
					borderColor: '#6366f1',
					backgroundColor: 'rgba(99,102,241,0.2)',
					fill: true,
					tension: 0.3
				}
			]
		};
	}, [transactions]);

	return (
		<div className="max-w-6xl mx-auto p-4 sm:p-8">
			<header className="flex justify-between items-center my-6">
				<div>
					<h1 className="text-3xl font-bold text-gray-800">My Finance Dashboard</h1>
					<p className="text-gray-500">Track and manage your money with ease.</p>
				</div>
				<div className="space-x-2">
					<button onClick={handleReset} className="btn-primary py-2 px-4 rounded-full">Reset</button>
					<button onClick={logout} className="btn-primary py-2 px-6 rounded-full font-semibold">Logout</button>
				</div>
			</header>

			{loading ? (
				<p className="text-gray-600">Loading...</p>
			) : (
				<main className="grid grid-cols-1 lg:grid-cols-3 gap-8">
					<div className="lg:col-span-2 space-y-8">
						<section className="card p-6">
							<h2 className="text-2xl font-semibold text-gray-700 mb-4">Summary</h2>
							<div className="summary-grid">
								<div className="summary-card bg-blue-50">
									<h3 className="text-sm font-medium text-gray-500">Total Balance</h3>
									<p className="text-2xl font-bold text-blue-600 mt-2">₹{balance.toFixed(2)}</p>
								</div>
								<div className="summary-card bg-green-50">
									<h3 className="text-sm font-medium text-gray-500">Total Income</h3>
									<p className="text-2xl font-bold text-green-600 mt-2">₹{totalIncome.toFixed(2)}</p>
								</div>
								<div className="summary-card bg-red-50">
									<h3 className="text-sm font-medium text-gray-500">Total Expenses</h3>
									<p className="text-2xl font-bold text-red-600 mt-2">₹{totalExpenses.toFixed(2)}</p>
								</div>
							</div>
							<div className="mt-4 flex items-center justify-between">
								<div className="text-gray-600">Monthly Budget:</div>
								<div className="text-lg font-bold text-blue-600">₹{Number(budget || 0).toFixed(2)}</div>
								<button onClick={handleSetBudget} className="btn-primary py-1 px-4 rounded-full">Set Budget</button>
							</div>
						</section>

						<section className="card p-6">
							<h2 className="text-2xl font-semibold text-gray-700 mb-4">Add New Transaction</h2>
							<form onSubmit={handleAdd} className="space-y-4">
								<div className="form-grid">
									<div>
										<label className="block text-sm font-medium text-gray-700">Description</label>
										<input
											className="input-field mt-1"
											placeholder="e.g., Groceries, Salary"
											value={form.description}
											onChange={e => setForm(f => ({ ...f, description: e.target.value }))}
											required
										/>
									</div>
									<div>
										<label className="block text-sm font-medium text-gray-700">Amount</label>
										<input
											type="number"
											className="input-field mt-1"
											placeholder="e.g., 50.00"
											min="0.01"
											step="0.01"
											value={form.amount}
											onChange={e => setForm(f => ({ ...f, amount: e.target.value }))}
											required
										/>
									</div>
								</div>
								<div className="form-grid">
									<div>
										<label className="block text-sm font-medium text-gray-700">Type</label>
										<select
											className="input-field mt-1"
											value={form.type}
											onChange={e => setForm(f => ({ ...f, type: e.target.value }))}
										>
											<option value="expense">Expense</option>
											<option value="income">Income</option>
										</select>
									</div>
									<div>
										<label className="block text-sm font-medium text-gray-700">Category</label>
										<select
											className="input-field mt-1"
											value={form.category}
											onChange={e => setForm(f => ({ ...f, category: e.target.value }))}
										>
											<option value="housing">Housing</option>
											<option value="food">Food</option>
											<option value="transportation">Transportation</option>
											<option value="entertainment">Entertainment</option>
											<option value="utilities">Utilities</option>
											<option value="other">Other</option>
										</select>
									</div>
								</div>
								<div className="flex justify-end">
									<button type="submit" className="btn-primary py-2 px-6 rounded-full font-semibold">Add Transaction</button>
								</div>
							</form>
						</section>

						<section className="card p-6">
							<h2 className="text-2xl font-semibold text-gray-700 mb-4">Recent Transactions</h2>
							<ul className="divide-y divide-gray-200">
								{transactions.length === 0 && (
									<li className="text-gray-500 text-center">No transactions yet.</li>
								)}
								{transactions.slice(0, 5).map(t => (
									<li key={t._id} className="p-2 flex justify-between">
										<span>{t.description} ({t.category})</span>
										<span className={t.type === 'income' ? 'text-green-600' : 'text-red-600'}>
											{t.type === 'income' ? '+' : '-'}₹{Number(t.amount).toFixed(2)}
										</span>
									</li>
								))}
							</ul>
						</section>
					</div>

					<div className="space-y-8">
						<section className="card p-6">
							<h2 className="text-2xl font-semibold text-gray-700 mb-4">Financial Overview</h2>
							<div className="space-y-8">
								<div className="h-64">
									<Pie data={pieData} options={{ plugins: { legend: { position: 'bottom' }, title: { display: true, text: 'Expense Divisions' } } }} />
								</div>
								<div className="h-64">
									<Bar data={barData} options={{ responsive: true, maintainAspectRatio: false }} />
								</div>
								<div className="h-64">
									<Line data={balanceLineData} options={{ responsive: true, maintainAspectRatio: false }} />
								</div>
							</div>
						</section>
					</div>
				</main>
			)}
		</div>
	);
}
