import { useState } from 'react';
import { login, register, setToken } from '../api';

export default function AuthModal({ onAuthenticated }) {
	const [isLoginMode, setIsLoginMode] = useState(true);
	const [username, setUsername] = useState('');
	const [password, setPassword] = useState('');
	const [loading, setLoading] = useState(false);
	const [message, setMessage] = useState('');

	async function handleSubmit(e) {
		e.preventDefault();
		setLoading(true);
		setMessage('');
		try {
			if (isLoginMode) {
				const data = await login(username, password);
				if (data?.token) {
					setToken(data.token);
					onAuthenticated();
				} else {
					setMessage(data?.message || 'Login failed.');
				}
			} else {
				await register(username, password);
				setMessage('Registration successful! Please login.');
				setIsLoginMode(true);
			}
		} catch (err) {
			setMessage('Server error.');
		} finally {
			setLoading(false);
		}
	}

	return (
		<div className="fixed inset-0 flex items-center justify-center bg-black/50 p-4">
			<div className="bg-white rounded-lg shadow-xl w-full max-w-md p-6">
				<h2 className="text-2xl font-semibold mb-4">{isLoginMode ? 'Login' : 'Register'}</h2>
				<form onSubmit={handleSubmit} className="space-y-4">
					<div>
						<label className="block text-sm font-medium text-gray-700">Username</label>
						<input
							type="text"
							className="mt-1 input-field"
							placeholder="Username"
							value={username}
							onChange={e => setUsername(e.target.value)}
							required
						/>
					</div>
					<div>
						<label className="block text-sm font-medium text-gray-700">Password</label>
						<input
							type="password"
							className="mt-1 input-field"
							placeholder="Password"
							value={password}
							onChange={e => setPassword(e.target.value)}
							required
						/>
					</div>
					<button
						type="submit"
						disabled={loading}
						className="btn-primary w-full py-2 rounded-full font-semibold"
					>
						{loading ? 'Please wait...' : (isLoginMode ? 'Login' : 'Register')}
					</button>
				</form>
				<p className="mt-4 text-sm text-gray-500">
					{isLoginMode ? "Don't have an account?" : 'Already have an account?'}{' '}
					<button
						onClick={() => setIsLoginMode(m => !m)}
						className="text-blue-600 font-semibold"
					>
						{isLoginMode ? 'Register' : 'Login'}
					</button>
				</p>
				{message && <p className="mt-4 text-sm text-gray-700">{message}</p>}
			</div>
		</div>
	);
}
