import { useState } from 'react';

// --- INLINE MOCK API ---
// Replaces '../api' for demonstration purposes
const delay = (ms) => new Promise(resolve => setTimeout(resolve, ms));

async function loginAPI(username, password) {
    await delay(800);
    // Mock validation: fail if password is 'error'
    if (password === 'error') return { message: 'Invalid credentials (mock).' };
    return { token: 'mock-jwt-token-' + Math.random().toString(36).substr(2) };
}

async function registerAPI(userData) {
    await delay(1000);
    console.log("Mock Registration Data:", userData);
    // Mock validation
    if (userData.username === 'exists') return { message: 'Username already taken.' };
    return { success: true };
}

function setToken(token) {
    if (!token) return;
    localStorage.setItem('mock_auth_token', token);
    console.log("Token set:", token);
}
// -----------------------

export default function AuthModal({ onAuthenticated = () => {} }) {
    const [isLoginMode, setIsLoginMode] = useState(true);
    const [loading, setLoading] = useState(false);
    const [message, setMessage] = useState({ text: '', type: '' }); // type: 'success' | 'error'

    // Form Fields
    const [formData, setFormData] = useState({
        username: '',
        password: '',
        firstName: '',
        lastName: '',
        email: ''
    });

    const handleChange = (e) => {
        setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
    };

    async function handleSubmit(e) {
        e.preventDefault();
        setLoading(true);
        setMessage({ text: '', type: '' });

        try {
            if (isLoginMode) {
                const data = await loginAPI(formData.username, formData.password);
                if (data?.token) {
                    setToken(data.token);
                    setMessage({ text: 'Login successful!', type: 'success' });
                    // Small delay to show success message before closing/redirecting
                    setTimeout(() => {
                        onAuthenticated();
                    }, 500);
                } else {
                    setMessage({ text: data?.message || 'Login failed.', type: 'error' });
                }
            } else {
                const res = await registerAPI(formData);
                if (res?.success) {
                    setMessage({ text: 'Registration successful! Please login.', type: 'success' });
                    setIsLoginMode(true);
                    // Clear sensitive fields
                    setFormData(prev => ({ ...prev, password: '' }));
                } else {
                    setMessage({ text: res?.message || 'Registration failed.', type: 'error' });
                }
            }
        } catch (err) {
            setMessage({ text: 'An unexpected error occurred.', type: 'error' });
        } finally {
            setLoading(false);
        }
    }

    const toggleMode = () => {
        setIsLoginMode(!isLoginMode);
        setMessage({ text: '', type: '' });
    };

    return (
        <div className="fixed inset-0 z-50 flex items-center justify-center p-4 animate-fade-in">
            {/* Backdrop with blur */}
            <div className="absolute inset-0 bg-slate-900/60 backdrop-blur-sm"></div>

            {/* Modal Container */}
            <div className="relative w-full max-w-md bg-white rounded-2xl shadow-[0_8px_30px_rgb(0,0,0,0.12)] overflow-hidden">
                {/* Decorative Header Bar */}
                <div className="h-2 bg-gradient-to-r from-indigo-500 to-purple-500"></div>

                <div className="p-8">
                    <div className="text-center mb-8">
                        <div className="inline-flex p-3 bg-indigo-50 rounded-2xl mb-4 text-indigo-600">
                            <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="w-8 h-8"><circle cx="12" cy="8" r="5"/><path d="M20 21a8 8 0 0 0-16 0"/></svg>
                        </div>
                        <h2 className="text-2xl font-bold text-slate-800">
                            {isLoginMode ? 'Welcome Back' : 'Create Account'}
                        </h2>
                        <p className="text-slate-500 text-sm mt-2">
                            {isLoginMode ? 'Enter your credentials to access your account' : 'Fill in your details to get started'}
                        </p>
                    </div>

                    {message.text && (
                        <div className={`mb-6 p-4 rounded-xl flex items-start gap-3 text-sm font-medium animate-slide-in ${
                            message.type === 'success' 
                                ? 'bg-emerald-50 text-emerald-700 border border-emerald-100' 
                                : 'bg-rose-50 text-rose-700 border border-rose-100'
                        }`}>
                            {message.type === 'success' 
                                ? <svg xmlns="http://www.w3.org/2000/svg" width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><path d="M22 11.08V12a10 10 0 1 1-5.93-9.14"/><polyline points="22 4 12 14.01 9 11.01"/></svg>
                                : <svg xmlns="http://www.w3.org/2000/svg" width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><circle cx="12" cy="12" r="10"/><line x1="12" x2="12" y1="8" y2="12"/><line x1="12" x2="12.01" y1="16" y2="16"/></svg>
                            }
                            {message.text}
                        </div>
                    )}

                    <form onSubmit={handleSubmit} className="space-y-5">
                        {!isLoginMode && (
                            <div className="grid grid-cols-2 gap-4 animate-fade-in-up">
                                <InputField 
                                    label="First Name" 
                                    name="firstName" 
                                    value={formData.firstName} 
                                    onChange={handleChange} 
                                    required={!isLoginMode} 
                                />
                                <InputField 
                                    label="Last Name" 
                                    name="lastName" 
                                    value={formData.lastName} 
                                    onChange={handleChange} 
                                />
                            </div>
                        )}

                        {!isLoginMode && (
                             <div className="animate-fade-in-up" style={{ animationDelay: '100ms' }}>
                                <InputField 
                                    label="Email Address" 
                                    type="email" 
                                    name="email" 
                                    value={formData.email} 
                                    onChange={handleChange} 
                                    required={!isLoginMode} 
                                />
                            </div>
                        )}

                        <InputField 
                            label="Username" 
                            name="username" 
                            value={formData.username} 
                            onChange={handleChange} 
                            required 
                        />
                        <InputField 
                            label="Password" 
                            type="password" 
                            name="password" 
                            value={formData.password} 
                            onChange={handleChange} 
                            required 
                        />

                        <button
                            type="submit"
                            disabled={loading}
                            className="w-full py-3 px-4 bg-indigo-600 hover:bg-indigo-700 text-white font-semibold rounded-xl shadow-md shadow-indigo-200 focus:ring-4 focus:ring-indigo-100 transition-all active:scale-[0.98] disabled:opacity-70 disabled:cursor-not-allowed flex justify-center items-center"
                        >
                            {loading ? (
                                <>
                                    <svg className="animate-spin -ml-1 mr-2 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                                        <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                                        <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                                    </svg>
                                    Please wait...
                                </>
                            ) : (isLoginMode ? 'Sign In' : 'Create Account')}
                        </button>
                    </form>

                    <div className="mt-8 pt-6 border-t border-slate-100 text-center">
                        <p className="text-slate-600">
                            {isLoginMode ? "Don't have an account?" : 'Already have an account?'}
                        </p>
                        <button
                            onClick={toggleMode}
                            className="mt-2 text-indigo-600 font-semibold hover:text-indigo-800 transition-colors"
                        >
                            {isLoginMode ? 'Register for free' : 'Sign in instead'}
                        </button>
                    </div>
                </div>
            </div>

            <style>{`
                @keyframes fadeIn { from { opacity: 0; } to { opacity: 1; } }
                @keyframes fadeInUp { from { opacity: 0; transform: translateY(10px); } to { opacity: 1; transform: translateY(0); } }
                @keyframes slideIn { from { opacity: 0; transform: translateX(-10px); } to { opacity: 1; transform: translateX(0); } }
                .animate-fade-in { animation: fadeIn 0.3s ease-out; }
                .animate-fade-in-up { animation: fadeInUp 0.4s ease-out forwards; }
                .animate-slide-in { animation: slideIn 0.3s ease-out forwards; }
            `}</style>
        </div>
    );
}

function InputField({ label, type = 'text', ...props }) {
    return (
        <div>
            <label className="block text-sm font-medium text-slate-700 mb-1.5 ml-1">
                {label}
            </label>
            <input
                type={type}
                className="w-full px-4 py-2.5 bg-slate-50 border border-slate-200 rounded-xl focus:bg-white focus:ring-2 focus:ring-indigo-500/20 focus:border-indigo-500 outline-none transition-all text-slate-800 placeholder-slate-400"
                {...props}
            />
        </div>
    );
}
